import React, { useState } from "react";
import SearchUI from "../ui/SearchUI";
import {API, API_DEFAULT_PARAMS}  from "../../../api/api";
import "./Search.css";

const SearchContainer = ({setData, setCity}) => {

    const [searchInput, setSearchInput] = useState('');
    const [showError, setShowError] = useState(false);

    const handleChange = (e) => {
        setSearchInput(e.target.value);
    };

    const handleSubmit = () => {
        setShowError(false);
        API.get('/', { 
            params: {
              ...API_DEFAULT_PARAMS,  
              city: searchInput
            }
        })
        .then((res)=>{
            if(res.status === 204){
                setShowError(true);
                setSearchInput('');
                setData(null);
            }
            else if(res.status === 400){
                setShowError(true);
                setSearchInput('');
                setData(null);
            }
            setCity(res.data.city_name);
            setData(res.data.data);
            setSearchInput('');
        })
        .catch((er)=>{
            console.error('error', er);
        })
    };

    return <SearchUI handleChange={handleChange} handleSubmit={handleSubmit} searchInput={searchInput} showError={showError}/>;
}

export default SearchContainer;
