import { months } from "../constans/months";

export function formateDate(date){
    //2021-01-14
    const arr = date.split('-');

    const month = months.find((el) => Object.keys(el) === String(arr[1]) &&  Object.values(el))
    console.log(month)
    // return <div>{arr[0]}<div>{arr[1].replace(/(..):(..):(.........)/, '$1:$2')}</div></div>
    return arr[2] + ' ' + arr[1] 
}
