import React from 'react';
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import SearchIcon from "@material-ui/icons/Search";
import IconButton from "@material-ui/core/IconButton";

const SearchUI = ({ searchInput, handleChange, handleSubmit, showError, classes }) => {
    return (
        // <div>
        //     <input 
        //         value={searchInput}
        //         onChange={handleChange}/>
        //     <button onClick={handleSubmit}>Узнать</button>
        //     {showError && 'Введите город'}
        // </div>

        <div
            style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                backgroundImage:
                    "url(https://images.pexels.com/photos/2929243/pexels-photo-2929243.jpeg?auto=compress&cs=tinysrgb&dpr=3&h=750&w=1260)",
                width: "100vw",
                height: "50vh",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat"
            }}
        >
            <Paper 
            className={classes.root}>
                <InputBase
                    className={classes.input}
                    inputProps={{ "aria-label": "search" }}
                    value={searchInput}
                    onChange={handleChange}
                />
                <IconButton
                    className={classes.iconButton}
                    aria-label="search"
                    onClick={() => handleSubmit()}
                >
                    <SearchIcon />
                </IconButton>
            </Paper>
            {showError && <div>Введите город</div>}
        </div>
    )
}

export default SearchUI
