import { months } from "../constans/months";

export function formateDate(date){
    //2021-01-14
    const arr = date.split('-');
    let num = [...arr[2]].shift();
    String(num) === 0 ? num = [...arr[2]][1] : num=arr[2];
    const month = months.find(el => (el.num === String(arr[1])) && el.month)
    console.log(num)
    // return <div>{arr[0]}<div>{arr[1].replace(/(..):(..):(.........)/, '$1:$2')}</div></div>
    return arr[2] + ' ' + month.month
}
